#include <stdio.h>
#include <string>
#include <unordered_set>
#include <bitset>

using namespace std;


//1 <= s.length <= 1000
//s[i] is either '0' or '1'.
//1 <= n <= 10^9

class Solution {
public:
    bool queryString(string s, int n) {
        unordered_set<string> StrSet;
        for(int end=0;end<s.size();end++){
            for(int begin=0;begin<=end;begin++){
                string str=s.substr(begin,end-begin+1);
                StrSet.insert(str);
            }
        }
        for(int i=n;i>n/2;i--){
            string b = bitset<32>(i).to_string();
            b=b.substr(b.find('1'));
            if(StrSet.find(b)==StrSet.end())return false;
        }
        return true;
    }
private:
   
};

int main(){
    Solution mysolu;
    string s="0110";
    int n=3;
    string s1="";
    bool res=mysolu.queryString(s, n);
    return 0;
}

