#include <stdio.h>
#include <string>
#include <vector>
#include <unordered_map>
#include <unordered_set>

using namespace std;

class Solution {
public:
    string longestDupSubstring(string s) {
        d=265;
        q= (long)(1 << 30) - 1;
        int begin=1;
        int end=s.size();
        int index=-1;
        int i=-1;
        while(begin<end){
            int mid=(begin+end)/2;
            int index= rabin_carp(s, mid);
            if(index==-1){
                end=mid;
            }else{
                begin=mid+1;
                i=index;
            }
        }
        if(i==-1) return "";
        else return s.substr(i,begin-1);
    }
private:
    int rabin_carp(string& s,int len){
        int res=-1;
        h=1;
        for(int i=0;i<len-1;i++){
            h=h*d%q;
        }
        long pattern=0;
        unordered_map<long,vector<int>> substrMap;
        for(int i=0;i<len;i++){
            pattern=(pattern*d+s[i])%q;
        }
        substrMap[pattern].push_back(0);
        for(int i=0;i<s.size()-len;i++){
            //remove the leading
            pattern-=h*s[i];
            //add the tail
            pattern=(pattern*d+s[i+len])%q;
            pattern=(pattern+q)%q;
            if(substrMap.find(pattern)!=substrMap.end()){
                for(int k:substrMap[pattern]){
                    string temp=s.substr(i+1,len);
                    if(s.substr(k,len)==temp){
                        res=i+1;
                        break;
                    }
                   

                }
              
            }
                substrMap[pattern].push_back(i+1);
        
        }
        return res;
    }
    
    
private:
    long h;
    long q;
    long d;
};



class Solution3 {
public:
    string longestDupSubstring(string s) {
        unordered_map<char,vector<int>> indexMap;
        pair<int,string> res(1,"");
        for(int i=0;i<s.size();i++){
            if(indexMap.find(s[i])!=indexMap.end()){
                for(int index:indexMap[s[i]]){
                    if(index-res.first<0||s.substr(index-res.first+1,res.first)!=s.substr(i-res.first+1,res.first))continue;
                    while(index-res.first>=0&&s[i-res.first]==s[index-res.first]){
                        res.first++;
                    }
                    res.second=s.substr(index-res.first+1,res.first);
                }
            }
            indexMap[s[i]].push_back(i);
        }
        return res.second;
    }
};

class Solution2 {
public:
    string longestDupSubstring(string s) {
        unordered_map<string,vector<int>> indexMap;
        int n=s.size();
        for(int i=0;i<n;i++) indexMap[s.substr(i,1)].push_back(i);
        int len=1;
        string res="";
        while(!indexMap.empty()){
            len++;
            unordered_map<string, vector<int>> temp;
            for(auto m:indexMap){
                if(m.second.size()==1) continue;
                else{
                    for(int index:m.second){
                        if(index+len>n) continue;
                        res=s.substr(index,len-1);
                        temp[s.substr(index,len)].push_back(index);
                    }
                }
            }
            indexMap=temp;
            
        }
        return res;
    }
};

